using dotnet_eval.Models;
using dotnet_eval.Services.InventoryServices;
using Microsoft.AspNetCore.Mvc;

namespace dotnet_eval.Controllers
{
    public class InventroyController : ControllerBase
    {
        private readonly IinventoryService InventoryService;
        public InventroyController (InventoryService inventoryService)
        {
            InventoryService = inventoryService;
        }
        //add: It will receive an object in json format and will save a user in the database and return a json of the user created with ID generated by the database
        [HttpPost("addItem")]
        public IActionResult AddItems(Inventory newItem)
        {
            InventoryService.AddItems(newItem);
            return Ok();
        }
        [HttpGet("GetItems")]
        public IActionResult GetItems(int itemId)
        {

            return Ok(InventoryService.GetItems(itemId));
        }

         [HttpGet("GetAllItems")]
        public IActionResult GetAllItems()
        {

            return Ok(InventoryService.GetAllItems());
        }

        [HttpGet("GetByUser")]
        public IActionResult GetByUser(int userId)
        {

            return Ok(InventoryService.GetByUser(userId));
        }

        /*[HttpPut("UpdateItems")]
        public IActionResult UpdateItems(Inventory itemsToUpdate)
        {
            InventoryService.UpdateItems(itemsToUpdate);
            return Ok();
        }*/
    }
}
