using dotnet_eval.Models;
using dotnet_eval.Services.UserServices;
using Microsoft.AspNetCore.Mvc;

namespace dotnet_eval.Controllers
{
    public class UserController : ControllerBase
    {
        private readonly IUserService UserService;
        public UserController (IUserService userService)
        {
            UserService = userService;
        }
        //add: It will receive an object in json format and will save a user in the database and return a json of the user created with ID generated by the database
        [HttpPost("addUser")]
        public IActionResult AddUser(User newUser)
        {
            UserService.AddUser(newUser);
            return Ok();
        }
        [HttpGet("GetUser")]
        public IActionResult GetUser(int id)
        {

            return Ok(UserService.GetUser(id));
        }

        [HttpPut("UpdateUser")]
        public IActionResult UpdateUser(User userToUpdate)
        {
            UserService.UpdateUser(userToUpdate);
            return Ok();
        }
        [HttpPut("UpdateKind/{id}/{kind}")]
        public IActionResult UpdateUserKind(int id, UserKind kind)
        {
            UserService.UpdateUserKind(id, kind);
            return Ok();
        }

    }
}
